mapper.update:
update anonymity
set a_name = #{aName,jdbcType=VARCHAR},
  a_hendpic = #{aHendpic,jdbcType=VARCHAR},
  a_addTime = #{aAddtime,jdbcType=VARCHAR}
where a_id = #{aId,jdbcType=INTEGER}

mapper.select:
select a_id, a_name, a_hendpic, a_addTime
from anonymity
where a_id = #{aId,jdbcType=INTEGER}
