  #{aCoverimg,jdbcType=LONGVARCHAR}, #{aSignature,jdbcType=VARCHAR}, #{aAddtime,jdbcType=TIMESTAMP},
  #{aSynopsis,jdbcType=LONGVARCHAR}, #{aSortId,jdbcType=INTEGER}, #{aSonId,jdbcType=INTEGER},#{aLabel,jdbcType=VARCHAR},
