mapper.select:
select d_id, d_name, d_headPic, d_content, d_site, d_like, d_browse, d_addTime,d_hot,imgUrl,videoUrl,d_label,d_num
from dynamic
where d_id = #{dId,jdbcType=INTEGER}

mapper.update:
update dynamic
 set  d_like = d_like + 1
where d_id = #{dId,jdbcType=INTEGER}

update dynamic
 set  d_browse = d_browse + 1
where d_id = #{dId,jdbcType=INTEGER}
